public class LibCalculator {

	public int add(int op1, int op2) {
		// TODO Auto-generated method stub
		
		if (op1<=0||op2<=0) {
			return 0;
		}
		return op1 + op2;
	}

	public int sub(int op1, int op2) {
		// TODO Auto-generated method stub
		return op1 - op2;
	}
	
	

}
